sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
   if(event.elementId == 'freq_dropdown'){
       if(event.payload.item.id == 'custom'){
           api.setState('hide_month_picker', false);
          // api.setState('schedule_new_val', '1');
       } else {
           api.setState('hide_month_picker', true);
           api.setState('schedule_new_val', event.payload.item.id);
       }
   }
   else if(event.elementId == 'month_dropdown'){
       api.setState('schedule_new_val', event.payload.item.id);
   }
}
