sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 *
function handler({api, event, helpers, imports}) {
    //sysId is the req'd parameter on employee-eval form.
    helpers.navigate.to('record',{sysId : event.payload.sys_id});
}*/
function handler({
    api,
    helpers,
    event,
    imports
}) {
    const {
        routeMapping,
        buildDefaultRoutePayload
    } = imports["global.recordRoutesMapping"]();
    var route = 'record';
    var targetRoute = '';
    const defaultRoutePayload = buildDefaultRoutePayload(route, targetRoute, event);
    const routeMap = api.data.dynamic_routing_list.routeMap;
    const payload = routeMapping(routeMap, defaultRoutePayload, event);
    const {
        emit
    } = api;
    emit('NAV_ITEM_SELECTED', payload);
}
