sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
    var pickerText = "";
    var pickerValue = "";
    switch(event.elementId){
        case "start_date_button":
            pickerText = "'Review period start'";
            pickerValue = api.state.start_new_val;
            break;
        case "ee_prompt_button":
            pickerText = "'Request employee self evaluation'";
            pickerValue = api.state.ee_prompt_new_val;
            break;
        case "notify_date_button":
            pickerText = "'Manager and employee receive reminder'";
            pickerValue = api.state.notify_new_val;
            break;
        case "deadline_button":
            pickerText = "'Evaluations due'";
            pickerValue = api.state.dealine_new_val;
            break;    
        case "end_date_button":
            pickerText = "'Review period end'";
            pickerValue = api.state.end_new_val;
            break; 
    }

    var pickerName = "Select the " + pickerText + " date";
    api.setState('date_picker_name', pickerName);
    api.setState('date_picker_value', pickerValue);
    api.setState('date_field', event.elementId);
}
